import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';

class editContact extends Component {
  constructor(props) {
    super(props);
    console.log(props);

    this.state = {
      fullname: '',
      email: '',
      number: '',
      location: '',
      date: '',
    };
  }

  //this is for CONTACT NUMBER LIMITS
  maxLengthCheck = (object) => {
    if (object.target.value.length > object.target.maxLength) {
      object.target.value = object.target.value.slice(0, object.target.maxLength)
    }
  }
  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  componentDidMount() {

    const cons_id = this.props.match.params.id;

    axios
      .get(`http://localhost:3400/contacts/view/${cons_id}`)
      .then((res) => {
        console.log(res.data);

        this.setState({
          fullname: res.data.fullname,
          email: res.data.email,
          number: res.data.number,
          location: res.data.location,
          date: res.data.date

        })

      })

  }


  onSubmit = e => {
    e.preventDefault();


    const cons_id = this.props.match.params.id;

    const data = {
      fullname: this.state.fullname,
      email: this.state.email,
      number: this.state.number,
      location: this.state.location,
      date: this.state.date,
    };

    console.log(data);

    axios
      .put(`http://localhost:3400/contacts/update/${cons_id}`, data)
      .then(() => {
        this.setState({
          fullname: '',
          email: '',
          number: '',
          location: '',
          date: '',
        })
        this.props.history.push('/');
        
      })
  };



  render() {
    return (
        <div className="container">
        <br />
        <form className="show-form">
            <div className="form-control">
                <label >ID</label>
                {<p>{contact.id}</p>}
            </div>
            <div className="form-control">
                <label >Full Name</label>
                {<p>{contact.fullname}</p>}
            </div>
            <div className="form-control">
                <label>Email Address</label>
                {<p>{contact.email}</p>}
            </div>
            <div className="form-control">
                <label>Contact Number</label>
                {<p>{contact.number}</p>}
            </div>
            <div className="form-control">
                <label>Location</label>
                {<p>{contact.location}</p>}
            </div>
            <div className="form-control">
                <label>Registered date</label>
                {<p>{contact.date}</p>}
            </div>

            <Link exact to='/'><button type="onclick" className="btn btn-block" value="Add Contact"  >Back</button></Link>
        </form>
    </div>
    );
  }
}


export default editContact;
