import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';

class add extends Component {
  constructor() {
    super(); // assigning an object to this.state.

    this.state = {
      fullname: '',
      email: '',
      number: '',
      location: '',
      date: '',
    };
  }
  //will change the provide value
  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = e => {
    e.preventDefault();

    //hold the current data
    const data = {
      fullname: this.state.fullname,
      email: this.state.email,
      number: this.state.number,
      location: this.state.location,
      date: this.state.date,
    };
    console.log(data);

    //Axios is used for adding data to MongoDB
    axios
      .post('http://localhost:3400/contacts/add', data)
      .then(res => {
        this.setState({
          fullname: '',
          email: '',
          number: '',
          location: '',
          date: '',
        })
        // console.log(this.props);
        this.props.history.push('/');
       
        
      })
      Swal.fire({
        icon: 'success',
        title: 'Yay...',
        text: 'You have successfully added a contact!'
    })

  }

  render() {
    return (
      <div>
        <div className="addContact">
          <div className="container ">
            <div id="createContainer" className="row ">
              <br />   <br />  <br />   <br />
              <div className="col-md-8 m-auto " >
                <Link to="/">
                  Back
                </Link>
              </div>
              <br />
              <div className="rounded col-md-8 m-auto border bg-white border-dark " id="format">
                <br />
                <p id="titleCreate" className="lead text-center">
                  Create new contact
                </p>
                <form onSubmit={this.onSubmit}>
                  <div className='form-group  row'>
                    <label for="fullnameLabel" class="col-sm-2 fs-bolder col-form-label">Fullname :</label>
                    <div class="col-sm-10">
                      <input required class="form-control" id="fullnameLabel"
                        type='text'
                        placeholder='Lastname, Firstname, Middle Innitial'
                        name='fullname'
                        className='form-control'
                        value={this.state.fullname}
                        onChange={this.onChange}
                      /> 
                    </div>
                  </div><br />
                  <div className='form-group row'>
                    <label for="emailLabel" class="col-sm-2  fs-bolder col-form-label">Email Add :</label>
                    <div class="col-sm-10">
                      <input required class="form-control" id="emailLabel" maxLength="45"
                        type='email'
                        placeholder='example@gmail.com'
                        name='email'
                        className='form-control'
                        value={this.state.email}
                        onChange={this.onChange}
                      />
                      {/*<i style={{ fontFamily: "Serif" }}>{this.state.errors.emailAddress2 || this.state.errors.emailAddress}</i>
    <i style={{ fontFamily: "Times New Roman" }}>{this.state.errors.emailAddress3}</i>*/}

                    </div></div>

                  <div className='form-group row'>
                    <label for="contactLabel" class="col-sm-2 fs-bolder col-form-label">Contact #:</label>
                    <div class="col-sm-10">
                      <input required class="form-control" id="contactLabel"
                        type='number'
                        placeholder='99999999999'
                        name='number'
                        className='form-control'
                        value={this.state.number}
                        onChange={this.onChange}
                      />{/*<i style={{ fontFamily: "Serif" }}>{this.state.errors.contactNumber || this.state.errors.contactNumber1 || this.state.errors.contactNumber2}</i> */}
                    </div></div><br />
                  <div className='form-group row'>
                    <label for="locationLabel" class="col-sm-2 fs-bolder col-form-label">Location:</label>
                    <div class="col-sm-10">
                      <select class="form-select" class="form-control" id="locationLabel" name='location' className='form-control' aria-label="Default select example" value={this.state.location}
                        onChange={this.onChange}>
                        <option value="">Select your location:</option>
                        <option value="Manila">Manila</option>
                        <option value="Cebu">Cebu</option>
                      </select>{/*<i style={{ fontFamily: "Serif" }}>{this.state.errors.location}</i>*/}
                    </div></div>

                  <div className='form-group row'>
                    <label for="registeredLabel" class="col-xs-3 fs-bolder col-form-label">Reg-Date:</label>
                    <div class="col-sm-10">
                      <input required class="form-control"
                        type='date'
                        placeholder='Enter registeredDate '
                        name='date' class="form-control" id="registeredLabel"
                        className='form-control'
                        value={this.state.date}
                        onChange={this.onChange}
                      />{/*<i style={{ fontFamily: "Serif" }}>{this.state.errors.registeredDate || this.state.errors.registeredDate2}</i>*/}
                    </div></div>
                  <input
                    type="submit"
                    onClick={this.onSubmit}
                    className="btn btn-outline-white btn-info btn-block mt-4 "
                  />
                  <br />
                </form>
              </div>
            </div>
          </div><br />
        </div>
      </div>
    );
  }
}

export default add;
