import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Swal from 'sweetalert2';
import { DataGrid } from '@mui/x-data-grid';
import { FaEye, FaPencilAlt, FaTimes } from 'react-icons/fa';


class showContact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contacts: []
    };
  }

  componentDidMount() {
    axios
      .get('http://localhost:3400/contacts')
      .then(res => {
        this.setState({
          contacts: res.data
        })
      })
      .catch(err => {
        console.log('Error from Contact List');
      })
  }



  render() {
    return (

      <div className="container" >
      <div className="col-md-11 m-auto " >
      <Link to="/add" className="btn btn-outline-primary float-left" id="showContactList">
       Add Contact
      </Link>
    </div>
        <div  className="container">
          <div className="">
            <h1 >Contact List</h1><br />

            <div style={{ height: 400 }}>
              <DataGrid style={{ color: 'black' }}
                rows={this.state.contacts}
                columns={columns}
                pageSize={5}
                rowsPerPageOptions={[5]}
                getRowId={(row) => row._id}
                disableSelectionOnClick
              />
            </div>
          </div></div>
      </div>
    );
  }
}

//Creating Column
const columns = [
  { field: '_id', headerName: 'ID', width: 150 },
  {
    field: 'fullname',
    headerName: 'Full Name',
    width: 150,
  },
  {
    field: 'email',
    headerName: 'Email Address',
    width: 200,
  },
  {
    field: 'number',
    headerName: 'Contact Number',
  },
  {
    field: 'location',
    headerName: 'Location',
  },
  {
    field: 'date',
    headerName: 'Registered Date',
    
  },
  {
    field: '',
    headerName: 'Action',
  


    renderCell: (params) => {

      const onDelete = async () => {
        
        axios
          .delete('http://localhost:3400/contacts/' + params.row._id)
          .then(() => {
            console.log(params.row._id);
            Swal.fire(
              'Successfully Deleted!',  
            );
            window.location.reload();
          })
          .catch(err => {
            console.log("Error in deleting contact!" + err);
          })
      };
      return (
        <>
          <Link exact to={`contacts/view/${params.row._id}`}><FaEye onClick={(params.row._id)} className="editIcon" /></Link>&nbsp;&nbsp;
          <Link exact to={`contacts/EditContacts/${params.row._id}`}><FaPencilAlt onClick={(params.row._id)} className="editIcon" /></Link>&nbsp;&nbsp;
          <FaTimes className="delIcon" /> &nbsp;
        </>
      );
    }
  }
];


export default showContact;
